mapper.sql:
SELECT
COUNT(1)
FROM
T_USER u
WHERE 1 = 1

    AND u.USERNAME = #{user.username}


    AND u.DEPT_ID = #{user.deptId}


    AND u.SEX = #{user.sex}


    AND u.STATUS = #{user.status}


    AND u.NAME = #{user.name}


    AND u.MOBILE = #{user.mobile}


    And u.CREATE_TIME > #{user.createTimeFrom}


    And u.CREATE_TIME < #{user.createTimeTo}

SELECT u.USER_ID userId, u.USERNAME, u.EMAIL, u.MOBILE, u.POST, u.USER_EMPNO userEmpno, u.STATUS, u.CREATE_TIME createTime, u.SSEX sex, u.DEPT_ID deptId, u.LAST_LOGIN_TIME lastLoginTime, u.MODIFY_TIME modifyTime, u.NAME, u.AVATAR, d.DEPT_NAME deptName, LISTAGG(r.ROLE_ID,',') WITHIN GROUP (ORDER BY r.ROLE_ID) AS roleId, LISTAGG(TO_CHAR(r.ROLE_NAME),',') WITHIN GROUP (ORDER BY TO_CHAR(r.ROLE_NAME)) AS roleName FROM T_USER u LEFT JOIN T_DEPT d ON (u.DEPT_ID = d.DEPT_ID) LEFT JOIN T_USER_ROLE ur ON (u.USER_ID = ur.USER_ID) LEFT JOIN T_ROLE r ON (r.ROLE_ID = ur.ROLE_ID) WHERE 1 = 1
AND u.USERNAME = #{user.username}
AND u.OAID = #{user.oaid}
AND d.DEPT_ID = #{user.deptId}
AND u.SEX = #{user.sex}
AND u.STATUS = #{user.status}
AND u.NAME like CONCAT(CONCAT('%',#{user.name}),'%')
AND u.MOBILE = #{user.mobile}
AND u.USER_EMPNO = #{user.userEmpno}
And u.CREATE_TIME > #{user.createTimeFrom}
And u.CREATE_TIME < #{user.createTimeTo}
GROUP BY u.USER_ID , u.USERNAME, u.EMAIL, u.MOBILE, u.POST, u.USER_EMPNO , u.STATUS, u.CREATE_TIME , u.SSEX, u.DEPT_ID , u.LAST_LOGIN_TIME , u.MODIFY_TIME , u.NAME, u.AVATAR, d.DEPT_NAME
